mapper.select:
SELECT 	SEQ, SUBJECT, CONTENT, WRITER, REGDATE, VIEWCNT
FROM	BBS_TBL
WHERE	SEQ = #{seq}

mapper.update:
UPDATE 	BBS_TBL
SET		SUBJECT = #{subject} , CONTENT = #{content}
WHERE	SEQ = #{seq}

			UPDATE BBS_TBL
			SET VIEWCNT = VIEWCNT + 1
 			WHERE SEQ = #{seq}

mapper.select:
SELECT 	RSEQ, SEQ, RWRITER, RCONTENT, RDATE
FROM	REPLY_TBL
WHERE	SEQ = #{seq}
